INSERT INTO products(name, description, category_id, price, image_path)
VALUES
    ('Nike x CLOT','Верхняя часть Air Force 1 "Royal University Blue Silk" изготовлена из синей шелковистой ткани, украшенной фирменным принтом на ткани. Принт вдохновлен тысячелетними китайскими узорами и повторяется на серо-коричневом кожаном слое, скрывающемся под верхом. Это символизирует философию инь-ян и общее послание о том, что красоту можно найти как снаружи, так и внутри. Дизайн новой модели дополнен лаконичным брендом CLOT и резиновой подошвой классического коричневого цвета. Для съемок короткого ролика в рамках рекламной кампании бренд привлек популярного китайского актера и мастера боевых искусств Донни Йена, который снялся в фильме "Изгой один". Звездные войны: истории", серия фильмов "Ип Ман" и другие блокбастеры.',1, 800, 'images/Nike x CLOT_prime.jpg'),
    ('Nike Air Max 270', 'Nike Air Max 270 - культовая модель для поклонников уличного стиля. Обычная воздушная вставка для этой линии увеличена здесь до максимального размера, что создает узнаваемый силуэт и обеспечивает первоклассную амортизацию. Обтекаемый дизайн отсылает нас к классике бега, но здесь нет стандартных ограничений спортивной обуви. Верх из износостойкой синтетики гарантирует идеальную посадку по ноге, а эластичная внутренняя вставка и продуманный дизайн носка также отвечают за удобство. Как и все лучшие кроссовки Nike, 270s нашли идеальный баланс между винтажными формами и самым современным технологическим оборудованием.', 1, 152, 'images/Nike Air Max 270_prime.jpg'),
    ('Nike Challenger OG', 'Нестареющая классика – первая партия этих кроссовок для бега была выпущена еще в 1979 году и с тех пор практически не изменилась. Строгая черно-белая расцветка сделала "челленджеры" отличным вариантом на каждый день, но со временем линейка разрослась до более ярких вариантов. Верх кроссовок представляет собой комбинацию сетки, нейлона и замши – это обеспечивает как малый вес, так и общий комфорт при ношении пары с весны по осень. Акцент здесь делается не на технологичности, а на общих представлениях об удобстве – мягкая вставка в области щиколотки, хорошая амортизация подошвы, простота ухода. Nike Challenger OG подходит как для мужчин, так и для женщин, размерная сетка предусматривает варианты даже для детей.', 1, 83, 'images/Nike Challenger OG_prime.jpg'),
    ('Nike React Vision', 'Здесь в полной мере использованы достижения бренда в области работы с текстилем: многослойный синтетический верх притягивает взгляд, но в то же время обеспечивает высочайший комфорт, поскольку полностью повторяет силуэт стопы. Пластиковый фиксатор каблука в тандеме с агрессивной подошвой создает неповторимый образ – React Vision действительно не спутаешь с аналогами. Внешний вид кроссовок выглядит так, как будто это результат ручного труда. Ну и куда без фирменного пеноматериала Nike React – дорога под ногами буквально не ощущается, даже если вы идете по ней далеко не первый час.', 1, 136 , 'images/Nike React Vision_prime.jpg'),
    ('Nike Air Force 1', 'Nike Air Force 1 - это квинтэссенция стиля семидесятых годов прошлого века. Здесь есть и милое ретро, и очевидные баскетбольные силуэты, и перфорация кожи, которая отсчитывает время как раз с того момента, когда кроссовки превращаются в обувь на каждый день. С точки зрения эксплуатационных характеристик "форсы" удобны и идеально подходят для демисезона: кожа вынослива, любые загрязнения с них легко удаляются обычной влажной тряпкой.Но его популярность только растет из года в год: дизайн действительно культовых кроссовок, таких как wine, со временем становится только лучше.', 1, 140, 'images/Nike Air Force 1_prime.jpg'),
    ('Vans x Sesame Street Style Oscar the Groach', 'Vans, оригинальный бренд экшн-спорта и мировая икона творческого поиска, гордится сотрудничеством с одной из самых впечатляющих телевизионных программ всех времен: "Улица Сезам". Подчеркивая общую направленность обоих брендов на творческие открытия и поддерживая идею “Мы все принадлежим друг другу”, коллекция Vans x "Улица Сезам" посвящена культовой улице, где дети растут умнее, сильнее и добрее.', 2, 90, 'images/Vans x Sesame Street Style Oscar the Groach_prime.jpg'),
    ('Vans CLASSIC SLIP-ON', 'Кроссовки Vans Classic Slip-On 98 DX заимствуют детали у оригинальных классических слипонов и обеспечивают повышенный комфорт благодаря усовершенствованным стелькам Ortholite®. В этих ботинках также присутствуют такие детали, как оригинальный номер фасона и цветовая палитра или переизданные принты, более глянцевая клейкая лента, оригинальный вес подкладки и прочный брезентовый верх с принтом в шахматном порядке для завершения образа.', 2, 70, 'images/Vans CLASSIC SLIP-ON_prime.jpg');
